const vcap = require('../../utils/vcap.js');

module.exports.add = meta => {
    meta.forEach(app => {
        if (app.sapCloudService) {
            vcap.add({
                service: app.sapCloudService,
                plan: 'default',
                appHostId: '12345678-1234-1234-1234-123456789abc',
                sapCloudService: app.sapCloudService
            });
        }
    });
};
